import React from 'react';

const ActivityTable = ({ activities, onCheckboxChange }) => {
  return (
    <table>
      <thead>
        <tr>
          <th>HR</th>
          <th>MIN</th>
          <th>SEC</th>
          <th>Event Number</th>
          <th>Activity Name</th>
          <th>Confirmed By</th>
          <th>Status</th>
        </tr>
      </thead>
      <tbody>
        {activities.map((activity, index) => (
          <tr key={index}>
            <td>{activity.hr}</td>
            <td>{activity.min}</td>
            <td>{activity.sec}</td>
            <td>{activity.eventNumber}</td>
            <td>{activity.activityName}</td>
            <td>{activity.confirmationBy}</td>
            <td>
              <input
                type="checkbox"
                checked={activity.status || false}
                onChange={() => onCheckboxChange(index)}
              />
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default ActivityTable;
