import React, { useState } from 'react';

const ActivityTable = ({ activities, onCheckboxChange }) => {
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 2; // Number of topics per page

  const getPaginatedTopics = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return activities.slice(startIndex, endIndex);
  };

  const getPageCount = () => {
    return Math.ceil(activities.length / itemsPerPage);
  };

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div>
      {getPaginatedTopics().map((activity, topicIndex) => (
        <div key={activity.activity_id}>
          <h2 style={{ fontWeight: 'bold' }}>{activity.activity_name}</h2>
          <table>
            <thead>
              <tr>
                <th>HR</th>
                <th>MIN</th>
                <th>SEC</th>
                <th>Event Number</th>
                <th>Event Name</th>
                <th>Confirmed By</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {activity.events.map((event, index) => (
                <tr key={event.event_id}>
                  <td>{event.hr}</td>
                  <td>{event.min}</td>
                  <td>{event.sec}</td>
                  <td>{event.event_number}</td>
                  <td>{event.event_name}</td>
                  <td>{event.confirmation_by}</td>
                  <td>
                    <input
                      type="checkbox"
                      checked={event.status || false}
                      onChange={() => onCheckboxChange(topicIndex, index)}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
      <div className="pagination">
        {Array.from({ length: getPageCount() }, (_, index) => (
          <button
            key={index}
            onClick={() => handlePageChange(index + 1)}
            className={index + 1 === currentPage ? 'active' : ''}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
};

export default ActivityTable;
