// src/App.js
import React, { useState, useEffect } from 'react';
import ActivityTable from './components/ActivityTable';
import ProgressBar from './components/ProgressBar';
import './App.css';

function App() {
  const [activities, setActivities] = useState([]);
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    fetch('http://localhost:5000/api/activities')
      .then((response) => response.json())
      .then((data) => {
        setActivities(data);
        updateProgress(data);
      });
  }, []);

  const updateProgress = (data) => {
    const completedActivities = data.filter((activity) => activity.status).length;
    const totalActivities = data.length;
    const progress = (completedActivities / totalActivities) * 100;
    setProgress(progress);
  };

  const handleCheckboxChange = (index) => {
    const newActivities = [...activities];
    newActivities[index].status = !newActivities[index].status;
    setActivities(newActivities);
    updateProgress(newActivities);
  };

  return (
    <div className="App">
      <h1>Activity Tracker</h1>
      <ActivityTable activities={activities} onCheckboxChange={handleCheckboxChange} />
      <ProgressBar progress={progress} />
    </div>
  );
}

export default App;
