import React, { useState } from 'react';

const ActivityTable = ({ activities, onCheckboxChange }) => {
  const [currentPages, setCurrentPages] = useState({});

  const itemsPerPage = 10;

  const getPaginatedData = (events, topicIndex) => {
    const currentPage = currentPages[topicIndex] || 1;
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return events.slice(startIndex, endIndex);
  };

  const getPageCount = (events) => {
    return Math.ceil(events.length / itemsPerPage);
  };

  const handlePageChange = (topicIndex, pageNumber) => {
    setCurrentPages(prevState => ({
      ...prevState,
      [topicIndex]: pageNumber
    }));
  };

  return (
    <div>
      {activities.map((activity, topicIndex) => (
        <div key={activity.activity_id}>
          <h2 style={{ fontWeight: 'bold' }}>{activity.activity_name}</h2>
          <table>
            <thead>
              <tr>
                <th>HR</th>
                <th>MIN</th>
                <th>SEC</th>
                <th>Event Number</th>
                <th>Event Name</th>
                <th>Confirmed By</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {getPaginatedData(activity.events, topicIndex).map((event, index) => (
                <tr key={event.event_id}>
                  <td>{event.hr}</td>
                  <td>{event.min}</td>
                  <td>{event.sec}</td>
                  <td>{event.event_number}</td>
                  <td>{event.event_name}</td>
                  <td>{event.confirmation_by}</td>
                  <td>
                    <input
                      type="checkbox"
                      checked={event.status || false}
                      onChange={() => onCheckboxChange(topicIndex, index)}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="pagination">
            {Array.from({ length: getPageCount(activity.events) }, (_, index) => (
              <button
                key={index}
                onClick={() => handlePageChange(topicIndex, index + 1)}
                className={index + 1 === (currentPages[topicIndex] || 1) ? 'active' : ''}
              >
                {index + 1}
              </button>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default ActivityTable;
