import React, { useState, useEffect } from 'react';
import ActivityTable from './components/ActivityTable';
import ProgressBar from './components/ProgressBar';
import './App.css';

function App() {
  const [activities, setActivities] = useState([]);
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    fetch('http://localhost:5000/api/activities')
      .then((response) => response.json())
      .then((data) => {
        console.log('Fetched activities:', data);
        if (Array.isArray(data)) {
          setActivities(data);
          updateProgress(data);
        } else {
          console.error('Fetched data is not an array:', data);
        }
      })
      .catch((error) => console.error('Error fetching activities:', error));
  }, []);

  const updateProgress = (data) => {
    const totalActivities = data.reduce((acc, topic) => acc + topic.events.length, 0);
    const completedActivities = data.reduce(
      (acc, topic) => acc + topic.events.filter((activity) => activity.status).length,
      0
    );
    const progress = totalActivities > 0 ? Math.round((completedActivities / totalActivities) * 100) : 0;
    setProgress(progress);
  };

  const handleCheckboxChange = (topicIndex, index) => {
    const newActivities = [...activities];
    newActivities[topicIndex].events[index].status = !newActivities[topicIndex].events[index].status;
    setActivities(newActivities);
    updateProgress(newActivities);
  };

  return (
    <div className="App">
      <h1>Activity Tracker</h1>
      <ActivityTable activities={activities} onCheckboxChange={handleCheckboxChange} />
      <ProgressBar progress={progress} />
    </div>
  );
}

export default App;
