import React, { useState } from 'react';

const ActivityTable = ({ activities, onCheckboxChange }) => {
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  const getPaginatedData = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return activities.flatMap(topic => topic.events).slice(startIndex, endIndex);
  };

  const getPageCount = () => {
    return Math.ceil(activities.flatMap(topic => topic.events).length / itemsPerPage);
  };

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Activity Topic</th>
            <th>HR</th>
            <th>MIN</th>
            <th>SEC</th>
            <th>Event Number</th>
            <th>Event Name</th>
            <th>Confirmed By</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {getPaginatedData().map((event, index) => (
            <tr key={index}>
              <td>{event.activity_name}</td>
              <td>{event.hr}</td>
              <td>{event.min}</td>
              <td>{event.sec}</td>
              <td>{event.event_number}</td>
              <td>{event.event_name}</td>
              <td>{event.confirmation_by}</td>
              <td>
                <input
                  type="checkbox"
                  checked={event.status || false}
                  onChange={() => onCheckboxChange(event.topicIndex, event.index)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="pagination">
        {Array.from({ length: getPageCount() }, (_, index) => (
          <button
            key={index}
            onClick={() => handlePageChange(index + 1)}
            className={index + 1 === currentPage ? 'active' : ''}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
};

export default ActivityTable;
